/*Table structure for table `app_article` */

DROP TABLE IF EXISTS `app_article`;

CREATE TABLE `app_article` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `title` varchar(130) COLLATE utf8_bin DEFAULT NULL,
  `categoryName` varchar(50) COLLATE utf8_bin DEFAULT NULL COMMENT '类别',
  `categoryId` bigint(20) DEFAULT NULL COMMENT '类别id',
  `status` int(11) DEFAULT '0' COMMENT '状态(0表示有效  1 表示无效  2 表示已删除)',
  `seoTitle` varchar(50) COLLATE utf8_bin DEFAULT NULL COMMENT 'seo文章标题',
  `seoKeyword` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT 'seo文章关键字',
  `seoDescribe` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT 'seo文章描述',
  `sort` int(11) DEFAULT NULL COMMENT '排序',
  `isStick` int(11) DEFAULT '0' COMMENT '是否置顶(0表示否  1 表示yes)',
  `content` text COLLATE utf8_bin COMMENT '文章正文',
  `isOutLink` int(11) NOT NULL DEFAULT '0' COMMENT '是否外链(0表示false 1表示true)',
  `outLink` varchar(255) COLLATE utf8_bin DEFAULT ' ---- ' COMMENT '外链地址',
  `titleImage` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '图片标题地址',
  `shortContent` text COLLATE utf8_bin COMMENT '文章简介',
  `writer` varchar(250) COLLATE utf8_bin DEFAULT NULL,
  `source` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '来源',
  `hits` int(11) DEFAULT '0' COMMENT '点击量',
  `created` datetime NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `modified` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  `saasId` varchar(255) COLLATE utf8_bin DEFAULT '' COMMENT 'saasId',
  `website` varchar(50) COLLATE utf8_bin DEFAULT 'cn' COMMENT 'cn表示中国站  en表示英文站',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=168 DEFAULT CHARSET=utf8 COLLATE=utf8_bin COMMENT='首页显示文章内容表表';

