/*Table structure for table `app_customer` */

DROP TABLE IF EXISTS `app_customer`;

CREATE TABLE `app_customer` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `username` varchar(100) COLLATE utf8_bin DEFAULT NULL,
  `passWord` varchar(255) COLLATE utf8_bin NOT NULL COMMENT '密码密码数字+英文+英文大小写  长度6-20',
  `accountPassWord` varchar(255) COLLATE utf8_bin DEFAULT '' COMMENT '密码密码数字+英文+英文大小写  长度6-20',
  `isLock` tinyint(4) DEFAULT '0' COMMENT '是否锁定   0没锁定  1锁定',
  `lockTime` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '锁定时间',
  `isDelete` tinyint(4) DEFAULT '0' COMMENT '是否禁用  0没有禁用  1禁用',
  `isReal` tinyint(4) DEFAULT '0' COMMENT '是否实名  0没有实名  1实名',
  `isRealUsd` tinyint(255) DEFAULT '0' COMMENT '是否实名  0没有实名  1实名',
  `salt` varchar(255) COLLATE utf8_bin DEFAULT '' COMMENT '盐',
  `userCode` varchar(50) COLLATE utf8_bin NOT NULL COMMENT '用户唯一标识字符串',
  `created` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `modified` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  `saasId` varchar(255) COLLATE utf8_bin NOT NULL COMMENT 'saasId',
  `referralCode` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `hasEmail` int(5) DEFAULT '0' COMMENT '邮箱是否校验',
  `integral` int(20) DEFAULT '0',
  `type` varchar(255) COLLATE utf8_bin DEFAULT '',
  `trueName` varchar(50) COLLATE utf8_bin DEFAULT NULL,
  `isChange` int(11) DEFAULT '0',
  `googleKey` varchar(100) COLLATE utf8_bin DEFAULT NULL COMMENT '谷歌验证码',
  `googleState` int(20) DEFAULT NULL,
  `isproving` int(20) DEFAULT NULL,
  `messIp` varchar(100) COLLATE utf8_bin DEFAULT NULL,
  `phoneState` int(20) DEFAULT NULL,
  `phone` varchar(20) COLLATE utf8_bin DEFAULT NULL,
  `googleDate` datetime DEFAULT NULL,
  `passDate` datetime DEFAULT NULL,
  `openPhone` int(100) DEFAULT NULL,
  `states` int(20) DEFAULT NULL,
  `uuid` varchar(200) COLLATE utf8_bin DEFAULT NULL COMMENT 'uuid',
  `commendCode` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `receCode` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `isAdmin` smallint(2) DEFAULT '0' COMMENT '是否管理员 0否 1是',
  `isGag` smallint(2) DEFAULT '0' COMMENT '是否禁言 0否 1是',
  `gagDate` datetime DEFAULT NULL COMMENT '禁言开始时间',
  `gagTime` int(10) DEFAULT NULL COMMENT '禁言时长',
  `checkStates` int(20) DEFAULT '0',
  `thirdUserName` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `platform` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `thirdUserPw` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `otcFrozenDate` datetime DEFAULT NULL COMMENT '冻结日期',
  `otcFrozenFlag` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '冻结标识',
  `otcFrozenCout` decimal(20,10) DEFAULT NULL COMMENT 'otc取消次数',
  `otcCancelDate` datetime DEFAULT NULL COMMENT 'OTC最新取消日期',
  `openOtcStates` int(20) DEFAULT '0' COMMENT '打开otc状态 0 关闭 1 打开',
  `mailStates` int(20) DEFAULT '0' COMMENT '绑定邮箱标识 0 未绑定 1 绑定',
  `mail` varchar(100) COLLATE utf8_bin DEFAULT NULL COMMENT '绑定的邮箱',
  `memberPoint` decimal(20,10) DEFAULT '0.0000000000' COMMENT '会员积分',
  PRIMARY KEY (`id`),
  KEY `app_customer_userName` (`username`)
) ENGINE=InnoDB AUTO_INCREMENT=6 DEFAULT CHARSET=utf8 COLLATE=utf8_bin COMMENT='客户主表';

