/*Table structure for table `app_dictionary` */

DROP TABLE IF EXISTS `app_dictionary`;

CREATE TABLE `app_dictionary` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `pid` int(11) DEFAULT NULL COMMENT '父节点ID',
  `path` varchar(50) DEFAULT NULL COMMENT '全路径',
  `itemName` varchar(50) DEFAULT NULL COMMENT '标题',
  `itemValue` varchar(50) DEFAULT NULL,
  `orderNo` int(11) DEFAULT NULL COMMENT '排序',
  `leaf` int(4) DEFAULT NULL COMMENT '叶子节点0非叶子节点，1叶子节点',
  `isOld` int(4) DEFAULT NULL COMMENT '是否删除0正常，1过期',
  `dicDescrption` varchar(255) DEFAULT NULL COMMENT '描述',
  `dicType` int(4) DEFAULT NULL COMMENT '0key不可更改，在代码里用来做唯一标识的，1，单级数据字典，2，多级数据字典',
  `dicKey` varchar(255) NOT NULL,
  `pDicKey` varchar(255) NOT NULL,
  `created` datetime DEFAULT NULL,
  `modified` datetime DEFAULT NULL,
  `saasId` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`id`),
  UNIQUE KEY `dicKey` (`dicKey`),
  UNIQUE KEY `dicKey_2` (`dicKey`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

