/*Table structure for table `c2c_transaction` */

DROP TABLE IF EXISTS `c2c_transaction`;

CREATE TABLE `c2c_transaction` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT,
  `transactionNum` varchar(100) COLLATE utf8_bin DEFAULT NULL COMMENT '交易订单号',
  `userName` varchar(100) COLLATE utf8_bin DEFAULT NULL COMMENT '用户登录名',
  `customerId` bigint(20) NOT NULL COMMENT '用户id',
  `accountId` bigint(20) NOT NULL COMMENT '虚拟账户id',
  `transactionType` smallint(2) DEFAULT NULL COMMENT '交易类型(1线上充值,2线上提现 3线下充值 4线下取现...)',
  `transactionMoney` decimal(20,10) DEFAULT NULL COMMENT '交易金额',
  `transactionCount` decimal(20,10) DEFAULT NULL COMMENT '交易金额',
  `transactionPrice` decimal(20,10) DEFAULT NULL COMMENT '交易金额',
  `status` smallint(2) DEFAULT NULL COMMENT '1待审核 2已完成 3以否决',
  `remark` varchar(20) COLLATE utf8_bin DEFAULT NULL COMMENT '备注',
  `coinCode` varchar(20) COLLATE utf8_bin DEFAULT '' COMMENT '币的类型(RMB USD)',
  `fee` decimal(20,10) DEFAULT '0.0000000000' COMMENT '手续费',
  `created` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '创建时间',
  `modified` datetime DEFAULT CURRENT_TIMESTAMP COMMENT '修改时间',
  `saasId` varchar(255) COLLATE utf8_bin NOT NULL COMMENT 'saasId',
  `businessmanId` bigint(20) NOT NULL,
  `businessmanBankId` bigint(20) NOT NULL,
  `randomNum` varchar(20) COLLATE utf8_bin DEFAULT '',
  `customerBankId` bigint(20) DEFAULT NULL,
  `transactionId` bigint(20) DEFAULT NULL,
  `status2` smallint(2) DEFAULT NULL,
  `businessman` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  `handleId` bigint(20) DEFAULT NULL COMMENT '管理人ID',
  `handleName` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '管理人用户名',
  `handleIp` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '管理人IP',
  `cardName` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '银行卡持有人',
  `cardNumber` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '银行卡号',
  `cardBank` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '开户银行',
  `subBank` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '卖家开户支行',
  `BusinessmanBankName` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '商家银行',
  `BusinessmanBankcard` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '商家银行卡号',
  `BusinessmanBankowner` varchar(255) COLLATE utf8_bin DEFAULT NULL COMMENT '商家',
  PRIMARY KEY (`id`)
