/*Table structure for table `ex_product_parameter` */

DROP TABLE IF EXISTS `ex_product_parameter`;

CREATE TABLE `ex_product_parameter` (
  `id` bigint(20) NOT NULL AUTO_INCREMENT COMMENT '主键id',
  `buyFeeRate` decimal(20,10) DEFAULT NULL COMMENT '买方手续费率',
  `sellFeeRate` decimal(20,10) DEFAULT NULL COMMENT '卖方手续费率',
  `buyMinMoney` decimal(20,10) DEFAULT NULL COMMENT '买方最小买入金额',
  `sellMinCoin` decimal(20,10) DEFAULT NULL COMMENT '卖方最小卖出币数',
  `state` int(3) DEFAULT '1' COMMENT '状态(是否可用  0 表示 没用  1表示有用  默认是1)',
  `name` varchar(10) COLLATE utf8_bin DEFAULT NULL COMMENT '每条参数的名称',
  `prepaidFeeRate` decimal(20,0) DEFAULT NULL COMMENT '充值手续费率',
  `paceFeeRate` decimal(20,0) DEFAULT NULL COMMENT '提现手续费率',
  `oneTimePaceNum` decimal(20,0) DEFAULT NULL COMMENT '每次提现的最大数量',
  `oneDayPaceNum` decimal(20,0) DEFAULT NULL COMMENT '每天提现的最大数量',
  `leastPaceNum` decimal(20,0) DEFAULT '0' COMMENT '最小提现 数量',
  `modified` datetime DEFAULT NULL COMMENT '修改时间',
  `created` datetime DEFAULT NULL COMMENT '创建时间',
  `saasId` varchar(255) COLLATE utf8_bin DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_bin COMMENT='产品参数表';

